import '../styles/App.css';
// Importamos useState desde React porque lo vamos a necesitar más abajo
import { useState } from 'react';

// Creamos la función o componente App. Lo creamos con una función arrow.
const App = () => {
  // Creo constante de estado counter y la función para modificarla setCounter.
  // El valor inicial que va a tener counter es 0.
  const [counter, setCounter] = useState(0);

  // Creo una función manejadora que se ejecutará cuando se cambie el input
  const handleInput = (ev) => {
    // Cogemos el valor del input
    counter += 1;

    // Ejecutamos la función setCounter para actualizar el valor del estado de counter
    // Cuando ejecutamos la función setCounter, React guarda el valor del input en la constante counter y re-renderiza la página.

    setCounter();
  };

  // Retornamos todo el código HTML que queremos que React pinte en la página.
  return (
    <div>
      <h1>Contador: {counter}</h1>
      <form>
        {/* En esta línea indicamos que cuando cambie el input se ejecute la función manejadora handleInput */}
        <input
          value="Suma uno"
          type="button"
          name="button"
          onChange={handleInput}
        />
      </form>
    </div>
  );
};

export default App;
