import '../styles/App.css';
//  Importamos useState desde React
import { useState } from 'react';

const App = () => {
  //4 -variable contador
  let counter = 0;

  const [number, setNumber] = useState(0);

  //3- creo función manejadora del botón incrementar
  const handleIncrement = () => {
    counter = number + 1;

    //ejecuto setNumber para actualizar el valor del estado number
    // React guarda el valor del counter en la constante number
    setNumber(counter);
  };

  return (
    <div>
      {/* 1- creo texto y le pongo la variable */}
      <h2>Contador: {number}</h2>
      {/* 2 - añado botón. Y le pongo evento y función handle al botón */}
      <button onClick={handleIncrement}>+</button>
    </div>
  );
};

export default App;
